    case &lt;-ctx.Done():
        ch &lt;- resp{
            id:  0,
            err: errors.New("request aborted due timeout"),
        }
    case &lt;-time.After(time.Second * time.Duration(rand.Intn(5))):
        ch &lt;- resp{
            id: rand.Int(),
        }
    }
}
